jQuery(document).ready(function($) {
    $('.baw_widgetarchives_widget_class ul.baw-months').hide();
    $('.baw-trigger').on('click', function(e) {
        $(this)
            .toggleClass('is-open')
            .closest('li')
            .find('ul.baw-months')
            .slideToggle(300);
    });
});
