jQuery(document).ready(function($) {
    $('.baw_widgetarchives_widget_class ul.baw-months').hide();
    $('.baw-trigger, .baw-header').on('click', function(e) {
        $(this)
            .closest('li')
            .find('ul.baw-months')
            .slideToggle(300);

        if ( $(this).hasClass('.baw-trigger') ) {
            $(this).toggleClass('is-open');
        }

        e.preventDefault();
    });
});
